import React, { useState } from 'react';
import axios from 'axios';
import './EditInfo.css'; // Keep your design intact

const EditInfo = () => {
    const [studentName, setStudentName] = useState('');
    const [studentId, setStudentId] = useState('');

    const handleNameChange = (e) => {
        setStudentName(e.target.value);
    };

    const handleIdChange = (e) => {
        setStudentId(e.target.value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Data to send to the backend
        const formData = {
            value: studentName, // New student name
            studentIdNO: studentId, // Student ID for reference
        };

        try {
            // Send a POST request to update the student name
            const response = await axios.post('http://localhost:8080/updateStudentName', formData);
            alert(response.data.message); // Success message
        } catch (error) {
            console.error('Error updating student details:', error);
            alert('Failed to update student details.');
        }
    };

    return (
        <div className="edit-info-container">
            <h1>Edit Student Information</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Student ID:
                    <input
                        type="text"
                        value={studentId}
                        onChange={handleIdChange}
                        placeholder="Enter Student ID"
                        required
                    />
                </label>
                <label>
                    Student Name:
                    <input
                        type="text"
                        value={studentName}
                        onChange={handleNameChange}
                        placeholder="Enter New Name"
                        required
                    />
                </label>
                <button type="submit">Update Name</button>
            </form>
        </div>
    );
};

export default EditInfo;
